import React, { useEffect, useState } from 'react';
import HomeView from './HomeView';
import { API_BASE_URL } from '../../constants';

const HomeContainer = () => {
  const [products, setProducts] = useState([]);
  const [coins, setCoins] = useState([]);
  const [loadingProducts, setLoadingProducts] = useState(false);
  const [loadingCoins, setLoadingCoins] = useState(false);
  const [reLoadingProducts, setReLoadingProducts] = useState(false);
  const [reLoadingCoins, setReLoadingCoins] = useState(false);
  const [loadingBuy, setLoadingBuy] = useState(false);
  const [runningTotal, setRunningTotal] = useState(0);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = () => {
    fetchCoins(true);
    fetchProducts(true);
  };

//https://e362c4af-faab-4d43-915c-65ee6a64adae.mock.pstmn.io/coins
  const fetchCoins = (init) => {
    init === true ? setReLoadingCoins(true) : setLoadingCoins(true);
    // TODO API_BASE_URL+'/coins'
    fetch('https://localhost:44383/vm/coins')
      .then((res) => res.json())
      .then((response) => {
        init === true ? setReLoadingCoins(false) : setLoadingCoins(false);
        setCoins(response);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const fetchProducts = (init) => {
    init === true ? setReLoadingProducts(true) : setLoadingProducts(true);
    // TODO API_BASE_URL+'/products'
    fetch('https://localhost:44383/vm/products', {
      method: 'get',
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then((res) => res.json())
      .then((response) => {
        init === true ? setReLoadingProducts(false) : setLoadingProducts(false);
        setProducts(response);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const addCoins = (coinValue) => {
    setRunningTotal(runningTotal + coinValue);
  };

  return (
    <HomeView
      functions={{
        loadData,
        fetchCoins,
        fetchProducts,
        addCoins
      }}
      state={{
        products,
        coins,
        loadingProducts,
        loadingCoins,
        reLoadingProducts,
        reLoadingCoins,
        loadingBuy,
        setLoadingBuy,
        runningTotal,
        setRunningTotal
      }}
    />
  );
};

export default HomeContainer;
