import React from 'react';
import { Button, Col, Container, Row } from 'reactstrap';
import DataTable from '../../components/DataTable';
import './HomeStyles.css';
import { API_BASE_URL } from '../../constants';

const HomeView = ({ functions, state }) => {
  const { loadData, fetchCoins, fetchProducts, addCoins } = functions;
  const {
    products,
    coins,
    loadingProducts,
    loadingCoins,
    reLoadingProducts,
    reLoadingCoins,
    loadingBuy,
    setLoadingBuy,
    runningTotal,
    setRunningTotal
  } = state;

  return !loadingBuy && !reLoadingProducts && !reLoadingCoins ? (
    <Container className="main-container" style={{ paddingTop: '100px' }}>
      <Row>
        <Col>
          <h3>Vending Machine</h3>
        </Col>
        <Col>
          <h3>Balance: R{runningTotal}</h3>
        </Col>
      </Row>
      <Row>
        <Col>
          <DataTable
            products={products}
            runningTotal={runningTotal}
            setLoadingBuy={setLoadingBuy}
            setRunningTotal={setRunningTotal}
            loadingProducts={loadingProducts}
          />
        </Col>
      </Row>
      <tbody>
        {!loadingCoins ? (
          coins.map((coin) => (
            <tr key={coin.name}>
              <Button color="success" onClick={() => addCoins(coin.value)}>
                Insert {coin.name}
              </Button>
            </tr>
          ))
        ) : (
          <img src="https://upload.wikimedia.org/wikipedia/commons/b/b1/Loading_icon.gif" />
        )}
      </tbody>
      <Button className="button-width" color="success" onClick={fetchProducts}>
        Reload products
      </Button>
      <Button className="button-width" color="success" onClick={fetchCoins}>
        Reload coins
      </Button>
      <Button className="button-width" color="success" onClick={loadData}>
        Refresh All
      </Button>
    </Container>
  ) : (
    <img src="https://upload.wikimedia.org/wikipedia/commons/b/b1/Loading_icon.gif" />
  );
};

export default HomeView;
