import React from 'react';
import { Button, Col, Container, Row } from 'reactstrap';
import DataTable from '../../components/DataTable';
import './HomeStyles.css';
import { API_BASE_URL } from '../../constants';

const HomeView = ({ functions, state }) => {
  const { loadData, fetchCoins, fetchProducts, addCoins } = functions;
  const {
    products,
    coins,
    loadingProducts,
    loadingCoins,
    reLoadingProducts,
    reLoadingCoins,
    loadingBuy,
    setLoadingBuy,
    runningTotal,
    setRunningTotal
  } = state;

  return !loadingBuy && !reLoadingProducts && !reLoadingCoins ? (
    <div  style={{ paddingTop: '100px', backgroundColor:"blue", flex: 1 }}>
    <Container className="main-container" style={{ paddingTop: '100px', backgroundColor:"yellow", flex: 1 }}>
      <Row>
        <Col>
          <h3>Vending Machine</h3>
        </Col>
        <Col>
          <h3>Balance: R{runningTotal}</h3>
        </Col>
      </Row>
      <Row style={{backgroundColor: 'green'}}>
        <Col style={{backgroundColor: 'red'}}>
          <DataTable
            products={products}
            runningTotal={runningTotal}
            setLoadingBuy={setLoadingBuy}
            setRunningTotal={setRunningTotal}
            loadingProducts={loadingProducts}
            fetchProducts={fetchProducts}
          />
        </Col>
      </Row>
      <tbody>
        {!loadingCoins ? (
          coins.map((coin) => (
            <tc className="button-padding" key={coin.name}>
              <Button color="success" onClick={() => addCoins(coin.value)}>
                Insert {coin.name}
                <br />
              </Button>


            </tc>
          ))
        ) : (
          <img src="https://upload.wikimedia.org/wikipedia/commons/b/b1/Loading_icon.gif" />
        )}
      </tbody>

        <br/>

      <Button className="btn-padding-right" color="success" onClick={fetchProducts}>
        Reload products

      </Button>
      <br/>
      <br/>
      <Button className="btn-padding-right" color="success" onClick={fetchCoins}>
        Reload coins
      </Button>
      <br/>
      <br/>
      <Button className="btn-padding-right" color="success" onClick={loadData}>
        Refresh All
      </Button>

    </Container>
    </div>
  ) : (
    <img src="https://upload.wikimedia.org/wikipedia/commons/b/b1/Loading_icon.gif" />
  );
};

export default HomeView;
